 SupportMapFragment mapFragment = (SupportMapFragment) getChildFragmentManager()
 .findFragmentById(R.id.map);
 if (mapFragment != null) {
 mapFragment.getMapAsync(this);
 }

 return view;
 }

 @Override
 public void onMapReady(@NonNull GoogleMap googleMap) {
